import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Optional, if you want to add styles

const App = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch products from the backend when the component mounts
  useEffect(() => {
    // Making a GET request to the backend API to fetch products
    axios.get('http://localhost:5002/api/products')
      .then((response) => {
        setProducts(response.data); // Store the fetched products in state
        setLoading(false); // Stop loading when data is fetched
      })
      .catch((err) => {
        setError('Error fetching products'); // Handle errors
        setLoading(false);
      });
  }, []); // Empty dependency array means this effect runs only once when the component mounts

  if (loading) {
    return <div>Loading...</div>; // Show loading message while waiting for data
  }

  if (error) {
    return <div>{error}</div>; // Show error message if something went wrong
  }

  return (
    <div className="App">
      <h1>Online Auction</h1>
      <h2>Products for Auction</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Price: ${product.price}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
